const joggResponse = $input.first().json;
const promptData = $('Extract Enhanced Prompt').first().json;

if (joggResponse.code === 0 && joggResponse.data?.video_id) {
  return {
    success: true,
    videoId: joggResponse.data.video_id,
    originalPrompt: promptData.originalPrompt,
    enhancedPrompt: promptData.enhancedPrompt,
    status: 'processing',
    message: 'Video creation started. Will be ready in 2-5 minutes.',
    checkStatusUrl: 'https://api.jogg.ai/v2/avatar_video/' + joggResponse.data.video_id
  };
} else {
  return {
    success: false,
    error: joggResponse.msg || 'Failed to create video',
    originalPrompt: promptData.originalPrompt,
    enhancedPrompt: promptData.enhancedPrompt
  };
}
